import React from "react";
// import Login from "./Components/Login/Login";
import { Route, Switch, withRouter } from "./../node_modules/react-router-dom";
import Menu from "./Components/Login/Components/Menu";
import {
  TransitionGroup,
  CSSTransition
} from "./../node_modules/react-transition-group";
import "./pageTransition/pageTransition.scss";
// import RegisterForm from "./Components/Login/Components/RegisterForm";
import WeatherApp from "./Components/WeatherApp/WeatherApp";
import NationalitiesApp from "./Components/NationalitiesApp/NationalitiesApp";
import CountryInfo from "./Components/NationalitiesApp/Components/CountryInfo";
import MoviesApp from "./Components/MoviesApp/MoviesApp";
import MovieDetails from "./Components/MoviesApp/Components/MovieDetails";
import Hero from "./Components/Hero/Hero";
import "./App.css";

const App = ({ location }) => {
  const timeout = { enter: 1200, exit: 1000 };
  return (
    <>
      <TransitionGroup component="div" className="App">
        <CSSTransition
          key={location.key}
          timeout={timeout}
          classNames="pageTransition"
          mountOnEnter={false}
          mountOnExit={true}
        >
          <Switch location={location}>
            <Route path={"/"} exact component={Menu} />
            <Route path={"/Hero"} exact component={Hero} />
            <Route path={"/WeatherApp"} exact component={WeatherApp} />
            <Route
              path={"/NationalitiesApp"}
              exact
              component={NationalitiesApp}
            />
            <Route
              path="/NationalitiesApp/:country"
              render={props => <CountryInfo {...props} />}
            />
            <Route path={"/MoviesApp"} exact component={MoviesApp} />
            <Route
              path={"/MoviesApp/:movie"}
              render={props => <MovieDetails {...props} />}
            />
            {/* <Route path={"/Register"} component={RegisterForm} /> */}
            {/* <Route path={"/"} exact component={Login} /> */}
          </Switch>
        </CSSTransition>
      </TransitionGroup>
    </>
  );
};

export default withRouter(App);
